const series = new Array();

data.series.map((s) => {
  const flowDirections = s.fields.find((f) => f.name === 'flow-direction').values.buffer || s.fields.find((f) => f.name === 'flow-direction').values;
  const srcAddrs = s.fields.find((f) => f.name === 'srcaddr').values.buffer || s.fields.find((f) => f.name === 'srcaddr').values;
  const dstAddrs = s.fields.find((f) => f.name === 'dstaddr').values.buffer || s.fields.find((f) => f.name === 'dstaddr').values;
  const values = s.fields.find((f) => f.name === 'bytes_percent (mean)').values.buffer || s.fields.find((f) => f.name === 'bytes_percent (mean)').values;

  const serieLinks = {};
  const existingNodes = [];
  const uniqueFlowDirection = Array.from(new Set(flowDirections));
  uniqueFlowDirection.map((d, i) => {
    series.splice(i, 1, {
      name: d,
      data: [],
      links: [],
    });
    serieLinks[i] = {};
    existingNodes.splice(i, 1, []);
  });

  values.map((value, i) => {
    let direction = flowDirections[i];
    let srcAddr = srcAddrs[i];
    let dstAddr = dstAddrs[i];

    const seriesIdx = uniqueFlowDirection.indexOf(direction);
    const links = serieLinks[seriesIdx]

    if (srcAddr in links && dstAddr in links) {
      const edgesInSrc = links[srcAddr]
      const edgesInDst = links[dstAddr]

      if (dstAddr in edgesInSrc) {
        edgesInSrc[dstAddr] = edgesInSrc[dstAddr] + value
      } else if (edgesInDst[srcAddr]) {
        edgesInDst[srcAddr] = edgesInDst[srcAddr] + value
      } else {
        edgesInSrc[dstAddr] = value
      }
    } else if (srcAddr in links || dstAddr in links) {
      if (dstAddr in links) {
        [srcAddr, dstAddr] = [dstAddr, srcAddr]
      }

      const edgesInSrc = links[srcAddr]

      if (dstAddr in edgesInSrc) {
        edgesInSrc[dstAddr] = edgesInSrc[dstAddr] + value
      } else {
        edgesInSrc[dstAddr] = value
      }
    } else {
      links[srcAddr] = { [dstAddr]: value }
    };


  });

  for (const [serieIdx, links] of Object.entries(serieLinks)) {
    for (const [srcAddr, edgesInSrc] of Object.entries(links)) {
      if (existingNodes[serieIdx].indexOf(srcAddr) == -1) {
        series[serieIdx].data.push({
          name: srcAddr,
        });

        existingNodes[serieIdx].push(srcAddr);
      };

      for (const [dstAddr, value] of Object.entries(edgesInSrc)) {
        if (existingNodes[serieIdx].indexOf(dstAddr) == -1) {
          series[serieIdx].data.push({
            name: dstAddr,
          });

          existingNodes[serieIdx].push(dstAddr);
        };

        series[serieIdx].links.push({
          source: srcAddr,
          target: dstAddr,
          value: (value * 100).toFixed(2),
        });
      }
    };
  }
});

return {
  legend: {
    data: series.map(a => a.name),
    selectedMode: 'single',
  },
  tooltip: {
    trigger: 'item',
    triggerOn: 'mousemove',
    formatter: function (params) {
      // console.info(params);
      var name = params.name;
      var value = params.value;
      return name + ': ' + value + ' %';
    },
  },
  series: series.map((d) => {
    return {
      name: d.name,
      type: 'sankey',
      data: d.data,
      links: d.links,
      emphasis: {
        focus: 'adjacency'
      },
      levels: [
        {
          depth: 0,
          itemStyle: {
            color: '#fbb4ae'
          },
          lineStyle: {
            color: 'source',
            opacity: 0.6
          }
        },
        {
          depth: 1,
          itemStyle: {
            color: '#b3cde3'
          },
          lineStyle: {
            color: 'source',
            opacity: 0.6
          }
        },
        {
          depth: 2,
          itemStyle: {
            color: '#ccebc5'
          },
          lineStyle: {
            color: 'source',
            opacity: 0.6
          }
        },
        {
          depth: 3,
          itemStyle: {
            color: '#decbe4'
          },
          lineStyle: {
            color: 'source',
            opacity: 0.6
          }
        }
      ],
      lineStyle: {
        curveness: 0.5
      }
    }
  }),
}

const xAxis = [];
const yAxis = [];
const graph = [];

data.series.map((s) => {
  const rows = s.fields.find((f) => f.type === 'string').values.buffer || s.fields.find((f) => f.type === 'string').values;
  const fields = s.fields.filter((f) => f.type === 'number');

  rows.map((d) => {
    xAxis.push(d);
  });

  fields.map((d, i) => {
    yAxis.push(d.name);
    d.values.map((v, j) => {
      graph.push([j, i, (v * 100).toFixed(0)])
    })
  });

});

return {
  tooltip: {
    position: 'top',
    formatter: function (param) {
      return `Bytes: ${param.data[2]} %`
    }
  },
  grid: {
    height: '50%',
    top: '10%'
  },
  xAxis: {
    type: 'category',
    data: xAxis,
    splitArea: {
      show: true
    },
    axisLabel: {
      formatter: function (value) {
        hourMapping = [
          '12a', '1a', '2a', '3a', '4a', '5a', '6a',
          '7a', '8a', '9a', '10a', '11a',
          '12p', '1p', '2p', '3p', '4p', '5p',
          '6p', '7p', '8p', '9p', '10p', '11p'
        ]
        return hourMapping[value];
      }
    }
  },
  yAxis: {
    type: 'category',
    data: yAxis,
    splitArea: {
      show: true
    },
    axisLabel: {
      formatter: function (value) {
        weekMapping = [
          undefined, 'Monday', 'Tuesday', 'Wednesday',
          'Thursday', 'Friday', 'Saturday', 'Sunday'
        ]
        return weekMapping[value];
      }
    }
  },
  visualMap: {
    min: 0,
    max: 10,
    calculable: true,
    orient: 'horizontal',
    left: 'center',
    bottom: '15%'
  },
  series: [
    {
      name: 'Punch Card',
      type: 'heatmap',
      data: graph,
      label: {
        show: true
      },
      emphasis: {
        itemStyle: {
          shadowBlur: 10,
          shadowColor: 'rgba(0, 0, 0, 0.5)'
        }
      }
    }
  ]
};
